import axios from "axios"

export const BaseUrl = "http://127.0.0.1:5000/api"

export const createCompanyApi = (companyInfo: Map<string,string>, companyLogo: File, wcloud: File):string => {
    const data = {
        'cname': companyInfo.get('companyName'),
        'address': companyInfo.get('companyAdr'),
        'sales': companyInfo.get('companySales'),
        'owner': companyInfo.get('companyCeo'),
        'info': companyInfo.get('companyContent'),
        'pay': companyInfo.get('companyPay'),
        'resign': companyInfo.get('companyResign'),
        'form': companyInfo.get('companyInfo'),
        'courl': companyInfo.get('companyUrl'),
        'user_type': 'admin'
    }

    const formData = new FormData();
    formData.append('companyLogo', companyLogo);
    formData.append('wcloud', wcloud);
    formData.append('data', new Blob([JSON.stringify(data)], {
        type: "application/json"
    }));

    axios.post( BaseUrl+'/company/create'
        , formData
        , {
            headers: {
                "Content-Type": "multipart/form-data"
            }
        }
    ).then(res => {
        return "OK"
    }).catch(err => {
        return "FAIL"
    });
    return ''
}
