import axios from "axios"
const BaseUrl = "http://127.0.0.1:5000/api"

export const loginApi = (id: string ,pw: string) => {
    const url = BaseUrl + "/user/login"
    axios.post(url, {
        headers: 
        {
            "Content-Type": "application/json"
        },
        body: { id: id, pw: pw }
    })
    .then(function(response) {
        console.log(response)
    })
    .catch(function(error) {
        console.log(error)
    })
}

export const createCompanyApi = (companyInfo: Map<string,string>, companyLogo: File, wcloud: File) => {
    const data = {
        'cname': companyInfo.get('companyName'),
        'address': companyInfo.get('companyAdr'),
        'sales': companyInfo.get('companySales'),
        'owner': companyInfo.get('companyCeo'),
        'info': companyInfo.get('companyContent'),
        'pay': companyInfo.get('companyPay'),
        'resign': companyInfo.get('companyResign'),
        'form': companyInfo.get('companyInfo'),
        'user_type': 'admin'
    }

    const formData = new FormData();
    formData.append('companyLogo', companyLogo);
    formData.append('wcloud', wcloud);
    formData.append('data', new Blob([JSON.stringify(data)], {
        type: "application/json"
    }));

    axios.post( BaseUrl+'/company/create'
        , formData
        , {
            headers: {
                "Content-Type": "multipart/form-data"
            }
        }
    ).then(res => {
        console.log(res);
    }).catch(err => {
        alert('등록을 실패하였습니다.');
    });
}
