import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { Stack } from '@mui/material';
import Card from '@mui/material/Card';
import { CardContent, CardMedia } from '@mui/material'
import Grid from '@mui/material/Unstable_Grid2';
import Icon from '../img/logo.svg'
import { experimentalStyled as styled } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom'



const Item = styled(Card)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: 'auto',
    maxWidth: 400,
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

const Info: React.FC = () => {
    const navigate = useNavigate();

    const goUpdate = () => {
      navigate('/info_update')
    };
    const goDelete = () => {
      navigate('/info_delete')
    };


    return (

        <div className='company_info'>
            <Box sx={{ display: 'flex',position:'relative', width:1500, height: 600, margin:'auto', border: 1, borderRadius: 5, backgroundColor:'#ffffff', flexDirection: 'column',mt:5, padding: 5 }} >
            <Box sx={{ flexGrow: 1, maxWidth: 1500, margin:0}}>
            <Grid container spacing={{ xs: 1, md: 3 }} columns={{ xs: 4, sm: 8, md: 8 }}>
            {Array.from(Array(1)).map((_, index) => (
                <Grid xs={2} sm={1} md={2} key={index}>
                <Item>
                    <CardContent>
                        <CardMedia
                            component="img"
                            sx={{ width: 300 }}
                            image={Icon}
                            alt="Paella dish"
                        />
                    </CardContent>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                        기업설명
                        </Typography>
                    </CardContent>
                </Item>
                </Grid>
            ))}
            <Stack>
             <Typography variant="h6"  sx={{ fontWeight:'bold', marginLeft:10,marginTop:5 }} >회사명 :  카카오</Typography>              
             <Typography variant="h6"  sx={{ fontWeight:'bold', marginLeft:10,marginTop:5 }}>홈페이지주소 : www.kakao.com</Typography>            
             <Typography variant="h6"  sx={{ fontWeight:'bold', marginLeft:10,marginTop:5 }}>기업정보 : 대기업</Typography>           
             <Typography variant="h6"  sx={{ fontWeight:'bold', marginLeft:10,marginTop:5 }}>기업요약 : 여러가지업종을 함</Typography>          
             <Typography variant="h6"  sx={{ fontWeight:'bold', marginLeft:10,marginTop:5 }}>대표 : 홍은택</Typography>        
             <Typography variant="h6"  sx={{ fontWeight:'bold', marginLeft:10,marginTop:5 }}>주소 : 성남</Typography>
            </Stack>
            </Grid>
        </Box>
            <Stack direction="row" spacing={2} sx={{mx:'auto'}}>
                    <Button variant="contained" sx={{ color:'#ffff', backgroundColor: '#5856D6', borderColor:'#434343'}} onClick={() => { goUpdate() }}>
                        수정
                    </Button>
                    <Button variant="outlined" sx={{color:'#ffff', backgroundColor: '#5856D6', borderColor:'#434343'}} onClick={() => { goDelete() }}>
                        삭제
                    </Button>
            </Stack>
            </Box>
        </div>
    );
}
export default Info;
